import {Component} from "react";
import React from "react";
import {Row} from "reactstrap";
import microphone from './microphone.png';


class Input extends Component {

  constructor() {
    super()
    this.state = {
      listening: false,
      text: '',
      lang: 'en-US',
      langue: 'EN'
    }
    this.toggleListen = this.toggleListen.bind(this)
    this.handleListen = this.handleListen.bind(this)
  }

  componentDidMount(): void {
    // eslint-disable-next-line no-undef
    const SpeechRecognition = SpeechRecognition || webkitSpeechRecognition
    this.recognition = new SpeechRecognition();

    this.recognition.continous = true;
    this.recognition.interimResults = true;
    this.recognition.lang = this.state.lang;
  }

  toggleListen() {
    this.setState({
      listening: !this.state.listening
    }, this.handleListen)
  }

  handleListen() {
    if (this.state.listening) this.recognition.start()

    let finalTranscript = ''
    this.recognition.onresult = event => {
      let interimTranscript = ''

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) finalTranscript += transcript + ' ';
        else interimTranscript += transcript;
      }
      // document.getElementById('interim').innerHTML = interimTranscript;
      // document.getElementById('final').innerHTML = finalTranscript;
      this.setState({text: finalTranscript})
      // handle speech recognition here
    }
  }

  onChange(e) {
    this.setState({text: e.target.value});
  }

  onSubmit(e) {
    e.preventDefault();
    this.setState({text: ""});
    if(this.state.text === '')
      return
    this.props.onSendMessage(this.state.text);
  }

  languppdate = (e) => {
    if(this.state.langue === 'EN') {
      this.setState({
        text: "",
        langue: 'VI',
        lang: 'vi',
      })
      e.preventDefault();
    }
    else {
      this.setState({
        text: "",
        lang: 'en-US',
        langue: 'EN'
      })
      e.preventDefault();
    }
  }

  render() {
    const { innerWidth: width, innerHeight: height } = window;
    console.log("111", this.state.lang)
    return (
      <div className="Input">
        <form >
          <div style={{width: width-100, backgroundColor: 'white', borderBottomWidth: 10 }}>
            <Row className="align-items-center">
              <input
                  style={{bordercolor: 'transparent', padding: 8, width: 500, marginBottom: 5, border: 'unset'}}
                  onChange={e => this.onChange(e)}
                  value={this.state.text}
                  type="text"
                  placeholder="Enter your message and press ENTER"
                  autoFocus="true"
              />
              <button onClick={this.toggleListen} style={{backgroundColor: 'transparent', border: 'unset'}} > <img src={microphone} alt="microphone" style={{width: 30, height: 30}}/></button>

              <button style={{backgroundColor: 'transparent', border: 'unset'}} onClick={(e)=> this.languppdate(e)} >{this.state.langue}</button>
              <button style={{backgroundColor: 'transparent', border: 'unset'}} onClick={(e)=> this.onSubmit(e)} >Send</button>
            </Row>
          </div>
        </form>
      </div>
    );
  }

}

export default Input;
