import {Component} from "react";
import React from "react";
import {Col, Row} from "reactstrap";
import CardText from "reactstrap/es/CardText";

class Messages extends Component {

  renderMessage(message) {
    const {member, text} = message;
    const {currentMember} = this.props;
    const messageFromMe = member.id === currentMember.id;
    const className = messageFromMe ?
      "Messages-message currentMember" : "Messages-message";
    // https://66.media.tumblr.com/bab328ff95d644edaa180a215acce3e3/tumblr_oo7b50QrGo1tq5811o1_500.jpg
    return (
      <div className={className} s style={{marginTop: 30}}>
       <Row>
         <img src={'https://66.media.tumblr.com/bab328ff95d644edaa180a215acce3e3/tumblr_oo7b50QrGo1tq5811o1_500.jpg'} style={{height: 50, width: 50, borderRadius: '50%',}} />
         <Col col="12" xl className="mb-3 mb-xl-0" style={{marginTop: -10}}
         >
           <CardText style={{color: 'gray'}}>{member.username}</CardText>

             <CardText style={{marginTop: -15, padding: 20, background: 'lightgray', width: 'fix',
               borderRadius: 13}} >{text}</CardText>

         </Col>
       </Row>
      </div>
    );
  }

  render() {
    const {messages} = this.props;
    return (
      <ul className="Messages-list">
        {messages.map(m => this.renderMessage(m))}
      </ul>
    );
  }
}

export default Messages;
