import {Component} from "react";
import React from "react";
import {Row} from "reactstrap";

class Messages extends Component {

  renderMessage(message) {
    const {member, text} = message;
    const {currentMember} = this.props;
    const messageFromMe = member.id === currentMember.id;
    const className = messageFromMe ?
      "Messages-message currentMember" : "Messages-message";
    // https://66.media.tumblr.com/bab328ff95d644edaa180a215acce3e3/tumblr_oo7b50QrGo1tq5811o1_500.jpg
    return (
      <div className={className}>
       <Row>
         <img src={'https://66.media.tumblr.com/bab328ff95d644edaa180a215acce3e3/tumblr_oo7b50QrGo1tq5811o1_500.jpg'} style={{height: 50, width: 50}} />
         <col>
           {member.username}
         </col>
       </Row>
        <div className="Message-content">
          <div className="text">{text}</div>
        </div>
      </div>
    );
  }

  render() {
    const {messages} = this.props;
    return (
      <ul className="Messages-list">
        {messages.map(m => this.renderMessage(m))}
      </ul>
    );
  }
}

export default Messages;
